   const { owner, repo, number } = context.issue

-  const commentBody = `Hey`;
+  const { TOTAL, PASSED, FAILED, FLAKY, SKIPPED, BASE_URL, REPORT_FOLDER } = process.env;
+
+  const commentTitle = `# Playwright Test Results ${FAILED > 0 ? '❌' : '✅'}`;
+  const commentBody = `
+  ${commentTitle}
+  ## Summary
